css:
.footer-bmd {
    background-color: #0D1B2A;
    color: #FFFFFF;
    padding: 6rem 1rem 3rem;
    font-family: sans-serif;
    position: relative;
}
.footer-bmd .container { max-width: 1200px; margin: 0 auto; text-align: center; }
.footer-bmd .company-name {
    font-size: clamp(3rem, 12vw, 8rem);
    font-weight: 900;
    line-height: 1;
    margin-bottom: 5rem;
    word-break: break-all;
}
.footer-bmd .footer-bottom {
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 1.5rem;
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.6);
}
.footer-bmd .links { display: flex; gap: 2rem; flex-wrap: wrap; }
.footer-bmd .links a { color: rgba(255, 255, 255, 0.8); text-decoration: none; }
@media (max-width: 768px) {
    .footer-bmd .footer-bottom { justify-content: center; text-align: center; }
    .footer-bmd .links { justify-content: center; }
}
