css:
.footer-centered-links {
    background-color: #f0f2f5; /* Light gray */
    padding: 2rem 1rem;
    text-align: center;
    font-family: sans-serif;
    color: var(--color-dark, #111827);
    position: relative;
}
.footer-centered-links .container {
    max-width: 1200px;
    margin: 0 auto;
}
.footer-centered-links .copyright {
    font-size: 0.9rem;
    margin-bottom: 0.5rem;
    color: var(--color-secondary, #6B7280);
}
.footer-centered-links .legal-links {
    display: flex;
    justify-content: center;
    gap: 1.5rem;
}
.footer-centered-links .legal-links a {
    font-size: 0.9rem;
    color: var(--color-dark, #111827);
    text-decoration: none;
    position: relative;
    padding-bottom: 2px;
}
.footer-centered-links .legal-links a::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 1px;
    background-color: var(--color-dark, #111827);
    transform: scaleX(0);
    transition: transform 0.3s ease;
}
.footer-centered-links .legal-links a:hover::after {
    transform: scaleX(1);
}
.footer-centered-links .anim-delay-1 { transition-delay: 0.1s; }
