css:
.footer-cdp { background-color: var(--color-dark, #0D1B2A); color: var(--color-light, #FFFFFF); padding: 5rem 1rem; font-family: sans-serif; position: relative; }
.footer-cdp .container { max-width: 1200px; margin: 0 auto; }
.footer-cdp .contact-top { display: flex; justify-content: flex-end; gap: 3rem; margin-bottom: 3rem; }
.footer-cdp .contact-link { font-size: 1.5rem; font-weight: 600; text-decoration: none; color: var(--color-light, #FFFFFF); }
.footer-cdp .footer-grid { display: grid; grid-template-columns: 1.5fr 1fr 1fr; gap: 3rem; }
.footer-cdp .logotype { font-size: 2.5rem; font-weight: bold; text-decoration: none; color: var(--color-light, #FFFFFF); display: block; margin-bottom: 1rem; }
.footer-cdp .footer-col-main > p { color: rgba(255,255,255,0.7); margin-bottom: 1.5rem; line-height: 1.6; font-size: 0.95rem; }
.footer-cdp .profile-card { display: flex; align-items: center; gap: 1rem; background: rgba(255,255,255,0.05); padding: 1rem; border-radius: 8px; margin-bottom: 1.5rem; }
.footer-cdp .profile-card img { width: 50px; height: 50px; border-radius: 50%; }
.footer-cdp .profile-card h4 { font-size: 1rem; font-weight: 600; margin: 0; }
.footer-cdp .profile-card p { font-size: 0.8rem; color: rgba(255,255,255,0.6); margin: 0; }
.footer-cdp .cta-button { display: inline-block; background: #1E293B; color: var(--color-light, #FFFFFF); padding: 0.75rem 1.5rem; border-radius: 8px; text-decoration: none; margin-bottom: 2rem; font-weight: 500; }
.footer-cdp .copyright { font-size: 0.8rem; color: rgba(255,255,255,0.5); }
.footer-cdp .legal-section { margin-top: 3rem; }
.footer-cdp h4 { font-size: 1.1rem; font-weight: bold; margin-bottom: 1.5rem; }
.footer-cdp .footer-col-links a { display: block; color: rgba(255,255,255,0.7); text-decoration: none; margin-bottom: 1rem; }
@media (max-width: 992px) { .footer-cdp .footer-grid { grid-template-columns: 1fr 1fr; } .footer-cdp .footer-col-main { grid-column: 1 / -1; } .footer-cdp .contact-top { justify-content: center; } }
@media (max-width: 576px) { .footer-cdp .footer-grid { grid-template-columns: 1fr; } }
