css:
.footer-csl {
    background: linear-gradient(180deg, #FFFFFF 0%, #F5F5DC 100%);
    color: var(--color-dark, #1F2937);
    padding: 4rem 1rem;
    font-family: sans-serif;
    position: relative;
}
.footer-csl .container {
    max-width: 1200px;
    margin: 0 auto;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
    text-align: center;
}
.footer-csl .icon-wrapper {
    width: 64px;
    height: 64px;
    background-color: #e5e7eb;
    border-radius: 12px;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0 auto 1.5rem;
    font-size: 1.5rem;
}
.footer-csl .main-text {
    font-size: 1.25rem;
    font-weight: 600;
    color: var(--color-dark, #1F2937);
    text-decoration: none;
    display: block;
}
.footer-csl .sub-text {
    font-size: 0.9rem;
    color: var(--color-secondary, #576574);
    margin-top: 0.5rem;
}
