css:
.section-footer-contact-v1 {
  position: relative;
  padding: 80px 0;
  background: #111;
  overflow: hidden;
  font-family: 'Helvetica Neue', sans-serif;
}

.section-footer-contact-v1 .bg-layer {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  opacity: 0.5;
}

.section-footer-contact-v1 .bg-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.section-footer-contact-v1 .container {
  position: relative;
  z-index: 2;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
}

.section-footer-contact-v1 .cards-grid {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 30px;
}

@media (max-width: 768px) {
  .section-footer-contact-v1 .cards-grid {
    grid-template-columns: 1fr;
  }
}

.section-footer-contact-v1 .contact-card {
  background: rgba(255, 255, 255, 0.9);
  padding: 30px 20px;
  text-align: center;
  border-radius: 4px;
  color: #333;
}

.section-footer-contact-v1 .contact-card h3 {
  font-size: 1.1rem;
  font-weight: 800;
  text-transform: uppercase;
  margin-bottom: 20px;
  letter-spacing: 1px;
}

.section-footer-contact-v1 .contact-card p {
  font-size: 0.9rem;
  line-height: 1.6;
  margin: 0;
}

.section-footer-contact-v1 .phone-link {
  font-weight: 700;
  color: #b83b12;
  text-decoration: underline;
  cursor: pointer;
  margin-bottom: 10px !important;
}

.section-footer-contact-v1 .footer-credit {
  text-align: center;
  color: rgba(255,255,255,0.6);
  font-size: 0.8rem;
  margin-top: 40px;
}
