css:
.footer-dcs {
    background-color: var(--color-dark, #0D1B2A);
    color: var(--color-light, #FFFFFF);
    padding: 4rem 1rem;
    font-family: sans-serif;
    position: relative;
}
.footer-dcs .container {
    max-width: 1200px;
    margin: 0 auto;
}
.footer-dcs .footer-grid { display: flex; justify-content: space-between; align-items: center; flex-wrap: wrap; gap: 3rem; margin-bottom: 3rem; }
.footer-dcs .contact-info { display: flex; flex-wrap: wrap; gap: 3rem; }
.footer-dcs .label { display: block; font-size: 0.9rem; color: rgba(255, 255, 255, 0.6); margin-bottom: 0.5rem; }
.footer-dcs .contact-link { font-size: 1.5rem; font-weight: 600; color: var(--color-light, #FFFFFF); text-decoration: none; }
.footer-dcs .social-icons-grid { display: grid; grid-template-columns: repeat(4, 1fr); gap: 0.75rem; }
.footer-dcs .social-icon { display: flex; align-items: center; justify-content: center; width: 48px; height: 48px; background-color: rgba(255, 255, 255, 0.05); border-radius: 8px; font-size: 1.25rem; color: var(--color-light, #FFFFFF); text-decoration: none; transition: background-color 0.3s; }
.footer-dcs .social-icon:hover { background-color: rgba(255, 255, 255, 0.1); }
.footer-dcs .telegram { grid-column: 1 / 5; margin-top: 0.75rem; }
.footer-dcs .copyright { font-size: 0.8rem; color: rgba(255, 255, 255, 0.5); }
@media (max-width: 768px) {
    .footer-dcs .footer-grid { justify-content: center; }
}
