css:
.footer-dms {
    background-color: var(--color-dark, #0D1B2A);
    color: var(--color-light, #FFFFFF);
    padding: 5rem 1rem;
    font-family: sans-serif;
    position: relative;
}
.footer-dms .container {
    max-width: 1200px;
    margin: 0 auto;
}
.footer-dms .footer-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 3rem;
}
.footer-dms .logotype {
    font-size: 2.5rem;
    font-weight: bold;
    text-decoration: none;
    color: var(--color-light, #FFFFFF);
    display: block;
    margin-bottom: 1rem;
}
.footer-dms .footer-col p {
    font-size: 0.95rem;
    color: rgba(255, 255, 255, 0.7);
    line-height: 1.6;
    margin-bottom: 2rem;
}
.footer-dms .footer-col h4 {
    font-size: 1rem;
    font-weight: bold;
    margin-bottom: 1.5rem;
    text-transform: uppercase;
    letter-spacing: 1px;
    color: rgba(255, 255, 255, 0.9);
    padding-bottom: 0.5rem;
    border-bottom: 1px solid rgba(255, 255, 255, 0.2);
}
.footer-dms .footer-col .sub-heading {
    border: none;
    padding-bottom: 0;
    margin-top: 2rem;
    margin-bottom: 1rem;
}
.footer-dms .footer-col a {
    display: block;
    color: rgba(255, 255, 255, 0.7);
    text-decoration: none;
    margin-bottom: 1rem;
    transition: color 0.3s;
}
.footer-dms .footer-col a:hover {
    color: var(--color-light, #FFFFFF);
}
.footer-dms .contact-item {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    color: rgba(255, 255, 255, 0.9);
    margin-bottom: 1rem;
}
