css:
.footer-dnbs {
    background-color: var(--color-dark, #0D1B2A);
    color: var(--color-light, #FFFFFF);
    padding: 4rem 1rem 2rem;
    font-family: sans-serif;
    position: relative;
}
.footer-dnbs .container { max-width: 1200px; margin: 0 auto; }
.footer-dnbs .footer-top {
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 2rem;
}
.footer-dnbs .logotype { font-size: 2rem; font-weight: bold; text-decoration: none; color: var(--color-light, #FFFFFF); }
.footer-dnbs .footer-nav { display: flex; flex-wrap: wrap; justify-content: flex-end; gap: 1rem 2rem; max-width: 500px; text-align: right; }
.footer-dnbs .footer-nav a { color: var(--color-light, #FFFFFF); text-decoration: none; opacity: 0.8; transition: opacity 0.3s; }
.footer-dnbs .footer-nav a:hover { opacity: 1; }
.footer-dnbs .footer-divider { border: none; height: 1px; background-color: rgba(255, 255, 255, 0.1); margin: 3rem 0; }
.footer-dnbs .footer-bottom { display: flex; justify-content: space-between; align-items: center; flex-wrap: wrap; gap: 1rem; }
.footer-dnbs .footer-bottom p { font-size: 0.9rem; color: rgba(255, 255, 255, 0.6); }
.footer-dnbs .social-icons { display: flex; gap: 1.5rem; }
.footer-dnbs .social-icons a { color: var(--color-light, #FFFFFF); font-size: 1.25rem; }
@media (max-width: 768px) {
    .footer-dnbs .footer-top, .footer-dnbs .footer-bottom { justify-content: center; text-align: center; }
    .footer-dnbs .footer-nav { justify-content: center; }
}
