css:
.footer-dark-nav {
    background-color: var(--color-dark, #0D1B2A);
    color: var(--color-light, #FFFFFF);
    padding: 4rem 1rem 2rem;
    font-family: sans-serif;
    position: relative;
}
.footer-dark-nav .container {
    max-width: 1200px;
    margin: 0 auto;
    text-align: center;
}
.footer-dark-nav .footer-top {
    display: grid;
    grid-template-areas: "logo" "nav" "social";
    gap: 2rem;
    justify-items: center;
    margin-bottom: 2rem;
}
@media (min-width: 768px) {
    .footer-dark-nav .footer-top {
        grid-template-areas: "logo nav social";
        grid-template-columns: 1fr auto 1fr;
        align-items: center;
    }
    .footer-dark-nav .logotype { justify-self: start; }
    .footer-dark-nav .social-icons { justify-self: end; }
}
.footer-dark-nav .logotype {
    grid-area: logo;
    font-size: 2rem;
    font-weight: bold;
    text-decoration: none;
    color: var(--color-light, #FFFFFF);
}
.footer-dark-nav .footer-nav {
    grid-area: nav;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: 1.5rem;
}
.footer-dark-nav .footer-nav a {
    color: var(--color-light, #FFFFFF);
    text-decoration: none;
    opacity: 0.8;
    transition: opacity 0.3s;
}
.footer-dark-nav .footer-nav a:hover {
    opacity: 1;
}
.footer-dark-nav .social-icons {
    grid-area: social;
    display: flex;
    gap: 1.5rem;
}
.footer-dark-nav .social-icons a {
    color: var(--color-light, #FFFFFF);
    font-size: 1.25rem;
    opacity: 0.8;
    transition: opacity 0.3s, transform 0.3s;
}
.footer-dark-nav .social-icons a:hover {
    opacity: 1;
    transform: scale(1.1);
}
.footer-dark-nav .footer-divider {
    border: none;
    height: 1px;
    background-color: rgba(255, 255, 255, 0.1);
    margin: 2rem 0;
}
.footer-dark-nav .footer-bottom {
    display: flex;
    flex-direction: column;
    justify-content: space-between;
    align-items: center;
    gap: 1rem;
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.6);
}
@media (min-width: 768px) {
    .footer-dark-nav .footer-bottom {
        flex-direction: row;
    }
}
.footer-dark-nav .legal-links {
    display: flex;
    gap: 1.5rem;
}
.footer-dark-nav .legal-links a {
    color: rgba(255, 255, 255, 0.8);
    text-decoration: none;
}
.footer-dark-nav .legal-links a:hover {
    text-decoration: underline;
}
.footer-dark-nav .anim-delay-1 { transition-delay: 0.1s; }
.footer-dark-nav .anim-delay-2 { transition-delay: 0.2s; }
