css:
.dark-footer .container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}
.dark-footer {
    background-color: #111827; /* gray-900 */
    color: #d1d5db; /* gray-300 */
    padding: 4rem 0 2rem;
    position: relative;
}
.dark-footer .footer-content {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    align-items: center;
    gap: 2rem;
    padding-bottom: 2rem;
    border-bottom: 1px solid #374151;
}
.dark-footer .footer-left h3 {
    font-size: 1.5rem;
    color: var(--color-light, #FFFFFF);
    margin-bottom: 0.5rem;
}
.dark-footer .newsletter-form {
    display: flex;
    margin-top: 1rem;
}
.dark-footer .newsletter-form input {
    padding: 0.75rem;
    border: 1px solid #374151;
    background-color: #1f2937;
    color: var(--color-light, #FFFFFF);
    border-radius: 6px 0 0 6px;
}
.dark-footer .newsletter-form button {
    padding: 0.75rem 1rem;
    background-color: var(--color-primary, #667eea);
    color: var(--color-light, #FFFFFF);
    border: none;
    border-radius: 0 6px 6px 0;
    cursor: pointer;
}
.dark-footer .social-icons {
    display: flex;
    gap: 1.5rem;
    font-size: 1.5rem;
}
.dark-footer .social-icons a {
    color: #d1d5db;
}
.dark-footer .footer-bottom {
    text-align: center;
    padding-top: 2rem;
    font-size: 0.9rem;
}
