css:
.footer-dwa { background-color: var(--color-dark, #0D1B2A); color: var(--color-light, #FFFFFF); padding: 5rem 1rem; font-family: sans-serif; position: relative; }
.footer-dwa .container { max-width: 1200px; margin: 0 auto; }
.footer-dwa .footer-grid { display: grid; grid-template-columns: 2fr 1fr 1fr; gap: 3rem; }
.footer-dwa .logotype { font-size: 2.5rem; font-weight: bold; text-decoration: none; color: var(--color-light, #FFFFFF); display: block; margin-bottom: 1rem; }
.footer-dwa p { font-size: 0.95rem; color: rgba(255, 255, 255, 0.7); line-height: 1.6; }
.footer-dwa .awards { display: flex; gap: 1rem; margin: 1.5rem 0; align-items: center; }
.footer-dwa .awards img { height: 25px; filter: contrast(0) brightness(2); }
.footer-dwa .sub-heading { font-size: 1.1rem; font-weight: bold; margin-bottom: 1rem; margin-top: 2rem; }
.footer-dwa .contact-item { display: flex; align-items: center; gap: 0.75rem; margin-bottom: 0.75rem; }
.footer-dwa .social-icons { display: flex; gap: 1rem; margin-top: 1.5rem; }
.footer-dwa .social-icon { width: 36px; height: 36px; display: flex; align-items: center; justify-content: center; background: rgba(255,255,255,0.1); border-radius: 8px; color: var(--color-light, #FFFFFF); text-decoration: none; }
.footer-dwa .links-col h4 { font-size: 1rem; font-weight: bold; margin-bottom: 1.5rem; text-transform: uppercase; letter-spacing: 1px; }
.footer-dwa .links-col a { display: block; color: rgba(255, 255, 255, 0.7); text-decoration: none; margin-bottom: 1rem; }
.footer-dwa .footer-bottom { border-top: 1px solid rgba(255,255,255,0.1); margin-top: 3rem; padding-top: 2rem; display: flex; justify-content: space-between; align-items: center; font-size: 0.8rem; color: rgba(255, 255, 255, 0.5); }
.footer-dwa .footer-bottom a { color: rgba(255, 255, 255, 0.5); margin-left: 1.5rem; }
@media (max-width: 992px) { .footer-dwa .footer-grid { grid-template-columns: 1fr 1fr; } .footer-dwa .about-col { grid-column: 1 / -1; } }
@media (max-width: 576px) { .footer-dwa .footer-grid { grid-template-columns: 1fr; } .footer-dwa .footer-bottom { flex-direction: column; gap: 1rem; } }
