css:
.footer-lcc {
    background: linear-gradient(180deg, #FFFFFF 0%, #F5F5DC 100%);
    color: var(--color-dark, #1F2937);
    padding: 4rem 1rem;
    font-family: sans-serif;
    text-align: center;
    position: relative;
}
.footer-lcc .container { max-width: 1200px; margin: 0 auto; }
.footer-lcc .logotype { font-size: 1rem; font-weight: 600; text-decoration: none; color: var(--color-dark, #1F2937); display: block; margin-bottom: 2rem; }
.footer-lcc .contact-info { margin-bottom: 2rem; }
.footer-lcc .contact-info a { display: block; font-size: 2rem; font-weight: 600; color: var(--color-dark, #1F2937); text-decoration: none; line-height: 1.3; }
.footer-lcc .social-links, .footer-lcc .legal-links { display: flex; justify-content: center; gap: 2rem; margin-bottom: 2rem; flex-wrap: wrap; }
.footer-lcc .social-links a { color: var(--color-dark, #1F2937); text-decoration: none; font-weight: 500; }
.footer-lcc .legal-links a { font-size: 0.9rem; color: var(--color-secondary, #576574); text-decoration: none; border-bottom: 1px solid #ccc; padding-bottom: 2px; }
