css:
.site-footer .container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}
.site-footer {
    background-color: var(--color-dark, #111827);
    color: var(--color-secondary, #9CA3AF);
    padding: 4rem 0 2rem;
    font-size: 0.9rem;
    position: relative;
}
.site-footer .footer-container {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: 2rem;
    padding-bottom: 2rem;
    border-bottom: 1px solid #374151;
}
.site-footer .footer-heading {
    font-size: 1rem;
    font-weight: 600;
    color: var(--color-light, #FFFFFF);
    margin-bottom: 1rem;
}
.site-footer .footer-links {
    list-style: none;
    padding: 0;
}
.site-footer .footer-links li {
    margin-bottom: 0.5rem;
}
.site-footer .footer-links a, .site-footer p a {
    color: var(--color-secondary, #9CA3AF);
    text-decoration: none;
}
.site-footer .footer-links a:hover, .site-footer p a:hover {
    color: var(--color-primary, #667eea);
}
.site-footer .social-icons {
    display: flex;
    gap: 1rem;
}
.site-footer .footer-bottom {
    text-align: center;
    padding-top: 2rem;
}
