css:
.footer-multicolumn-contact {
    background-color: var(--color-dark, #0D1B2A);
    color: var(--color-light, #FFFFFF);
    padding: 5rem 1rem;
    font-family: sans-serif;
    position: relative;
}
.footer-multicolumn-contact .container {
    max-width: 1200px;
    margin: 0 auto;
}
.footer-multicolumn-contact .footer-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
    gap: 2.5rem;
}
.footer-multicolumn-contact .logotype {
    font-size: 2rem;
    font-weight: bold;
    text-decoration: none;
    color: var(--color-light, #FFFFFF);
    display: block;
    margin-bottom: 1rem;
}
.footer-multicolumn-contact .footer-col p {
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.7);
    line-height: 1.6;
    margin-bottom: 1rem;
}
.footer-multicolumn-contact .copyright {
    font-size: 0.8rem;
    margin-top: 2rem;
}
.footer-multicolumn-contact .footer-col h4 {
    font-size: 1.1rem;
    font-weight: bold;
    margin-bottom: 1.5rem;
    color: var(--color-light, #FFFFFF);
}
.footer-multicolumn-contact .footer-col a {
    display: block;
    color: rgba(255, 255, 255, 0.7);
    text-decoration: none;
    margin-bottom: 0.75rem;
    transition: color 0.3s;
}
.footer-multicolumn-contact .footer-col a:hover {
    color: var(--color-light, #FFFFFF);
}
.footer-multicolumn-contact .contact-col {
    grid-column: span 1;
}
@media (min-width: 768px) {
    .footer-multicolumn-contact .footer-col:first-child { grid-column: span 2; }
    .footer-multicolumn-contact .contact-col { grid-column: span 2; }
}
@media (min-width: 992px) {
    .footer-multicolumn-contact .footer-col:first-child { grid-column: span 1; }
    .footer-multicolumn-contact .contact-col { grid-column: span 1; }
}
.footer-multicolumn-contact .contact-item {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    color: rgba(255, 255, 255, 0.9);
    margin-bottom: 1rem;
}
.footer-multicolumn-contact .contact-item i {
    color: var(--color-light, #FFFFFF);
}
