css:
.subscription-footer {
    background-color: #f3f4f6; /* gray-100 */
    padding: 4rem 0 2rem;
    text-align: center;
    position: relative;
}
.subscription-footer .container {
    max-width: 600px;
    margin: 0 auto;
    padding: 0 1.5rem;
}
.subscription-footer h2 {
    font-size: 2rem;
    color: var(--color-dark, #111827);
    margin-bottom: 1rem;
}
.subscription-footer p {
    color: var(--color-secondary, #6B7280);
    margin-bottom: 2rem;
}
.subscription-footer .newsletter-form {
    display: flex;
    gap: 0.5rem;
    margin-bottom: 3rem;
}
.subscription-footer .newsletter-form input {
    flex-grow: 1;
    padding: 0.8rem 1rem;
    border: 1px solid #d1d5db; /* gray-300 */
    border-radius: 8px;
    font-size: 1rem;
}
.subscription-footer .btn-primary {
    padding: 0.8rem 1.5rem;
    background-color: var(--color-primary, #667eea);
    color: var(--color-light, #FFFFFF);
    border: none;
    border-radius: 8px;
    cursor: pointer;
}
.subscription-footer .footer-bottom {
    font-size: 0.9rem;
    color: var(--color-secondary, #6B7280);
}
