css:
.footer-3col-complex {
    background-color: var(--color-dark, #0D1B2A);
    color: var(--color-light, #FFFFFF);
    padding: 5rem 1rem 2rem;
    font-family: sans-serif;
    position: relative;
}
.footer-3col-complex .container {
    max-width: 1200px;
    margin: 0 auto;
}
.footer-3col-complex .footer-main-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
    gap: 2.5rem;
    padding-bottom: 3rem;
    border-bottom: 1px solid rgba(255, 255, 255, 0.1);
}
.footer-3col-complex .logotype {
    font-size: 2.5rem;
    font-weight: bold;
    text-decoration: none;
    color: var(--color-light, #FFFFFF);
    display: block;
    margin-bottom: 1rem;
}
.footer-3col-complex .footer-col p {
    font-size: 0.95rem;
    color: rgba(255, 255, 255, 0.7);
    line-height: 1.6;
    margin-bottom: 1.5rem;
}
.footer-3col-complex .social-icons {
    display: flex;
    gap: 1.5rem;
}
.footer-3col-complex .social-icons a {
    color: var(--color-light, #FFFFFF);
    font-size: 1.25rem;
    transition: color 0.3s;
}
.footer-3col-complex .social-icons a:hover {
    color: var(--color-primary, #667eea);
}
.footer-3col-complex .footer-col h4 {
    font-size: 1rem;
    font-weight: bold;
    margin-bottom: 1.5rem;
    text-transform: uppercase;
    letter-spacing: 1px;
}
.footer-3col-complex .footer-col a {
    display: block;
    color: rgba(255, 255, 255, 0.7);
    text-decoration: none;
    margin-bottom: 1rem;
    transition: color 0.3s;
}
.footer-3col-complex .contact-item {
    display: flex;
    align-items: center;
    gap: 0.75rem;
    color: rgba(255, 255, 255, 0.9);
    margin-bottom: 1rem;
}
.footer-3col-complex .footer-bottom {
    display: flex;
    justify-content: space-between;
    align-items: center;
    flex-wrap: wrap;
    gap: 1rem;
    padding-top: 2rem;
    font-size: 0.9rem;
    color: rgba(255, 255, 255, 0.6);
}
.footer-3col-complex .legal-links {
    display: flex;
    gap: 1.5rem;
}
.footer-3col-complex .legal-links a {
    text-decoration: underline;
}
