css:
.footer-disclaimer {
    background-color: var(--color-dark, #0D1B2A);
    color: var(--color-light, #FFFFFF);
    padding: 5rem 1rem 2rem;
    font-family: sans-serif;
    position: relative;
}
.footer-disclaimer .container {
    max-width: 1200px;
    margin: 0 auto;
}
.footer-disclaimer .footer-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(240px, 1fr));
    gap: 3rem;
    margin-bottom: 4rem;
}
.footer-disclaimer .logotype {
    font-size: 2.5rem;
    font-weight: bold;
    text-decoration: none;
    color: var(--color-light, #FFFFFF);
    display: block;
    margin-bottom: 1.5rem;
}
.footer-disclaimer .address {
    font-size: 0.95rem;
    color: rgba(255, 255, 255, 0.7);
    line-height: 1.6;
    margin-bottom: 2rem;
}
.footer-disclaimer .footer-col h4 {
    font-size: 1.1rem;
    font-weight: bold;
    margin-bottom: 1.5rem;
    color: var(--color-light, #FFFFFF);
}
.footer-disclaimer .footer-col a {
    display: block;
    color: rgba(255, 255, 255, 0.7);
    text-decoration: none;
    margin-bottom: 1rem;
    transition: color 0.3s;
}
.footer-disclaimer .footer-col a:hover {
    color: var(--color-light, #FFFFFF);
}
.footer-disclaimer .footer-bottom {
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    align-items: flex-end;
    gap: 2rem;
    padding-top: 2rem;
    border-top: 1px solid rgba(255, 255, 255, 0.1);
    font-size: 0.8rem;
    color: rgba(255, 255, 255, 0.5);
}
.footer-disclaimer .disclaimer {
    flex: 2 1 400px; /* Allow disclaimer to take more space */
    max-width: 600px;
}
