css:
.contact-form-section .container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}
.contact-form-section { padding: 4rem 0; background-color: var(--color-light, #FFFFFF); }
.contact-form-section .contact-container {
    display: grid;
    grid-template-columns: 1fr;
    gap: 3rem;
    max-width: 1000px;
}
@media (min-width: 768px) { .contact-form-section .contact-container { grid-template-columns: 1fr 1fr; } }
.contact-info h2 { font-size: 2.5rem; margin-bottom: 1rem; color: var(--color-dark, #111827); }
.contact-info p { color: var(--color-secondary, #6B7280); margin-bottom: 2rem; }
.contact-info .info-item { display: flex; align-items: start; gap: 1rem; margin-bottom: 1rem; }
.contact-info .info-item span { font-size: 1.5rem; color: var(--color-primary, #667eea); }

.contact-form { background: var(--color-white, #fff); padding: 2rem; border-radius: 12px; box-shadow: 0 10px 30px rgba(0,0,0,0.1); }
.contact-form .form-group { margin-bottom: 1.5rem; }
.contact-form label { display: block; font-weight: 500; margin-bottom: 0.5rem; }
.contact-form input, .contact-form textarea {
    width: 100%;
    padding: 0.75rem;
    border: 1px solid #e0e0e0;
    border-radius: 8px;
    font-family: inherit;
    font-size: 1rem;
}
.contact-form input:focus, .contact-form textarea:focus { outline: none; border-color: var(--color-primary, #667eea); box-shadow: 0 0 0 3px rgba(102, 126, 234, 0.2); }
.contact-form .btn-submit {
    width: 100%;
    padding: 1rem;
    background-color: var(--color-primary, #667eea);
    color: var(--color-light, #FFFFFF);
    border: none;
    border-radius: 8px;
    font-size: 1.1rem;
    font-weight: 600;
    cursor: pointer;
}
