css:
.gallery-3d-cards-section {
  --card-height: 300px;
  --card-width: calc(var(--card-height) / 1.5);
}
.gallery-3d-cards-section * {
  box-sizing: border-box;
}
.gallery-3d-cards-section .cards-wrapper {
  width: 100%;
  min-height: 50vh;
  margin: 0;
  display: flex;
  flex-wrap: wrap;
  gap: 30px;
  justify-content: center;
  align-items: center;
  background: #191c29;
  padding: 3rem;
}
.gallery-3d-cards-section .card {
  width: var(--card-width);
  height: var(--card-height);
  position: relative;
  display: flex;
  justify-content: center;
  align-items: flex-end;
  padding: 0 36px;
  perspective: 2500px;
  margin: 0 50px;
}

.gallery-3d-cards-section .cover-image {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.gallery-3d-cards-section .wrapper {
  transition: all 0.5s;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  z-index: -1;
}

.gallery-3d-cards-section .card:hover .wrapper {
  transform: perspective(900px) translateY(-5%) rotateX(25deg) translateZ(0);
  box-shadow: 2px 35px 32px -8px rgba(0, 0, 0, 0.75);
}

.gallery-3d-cards-section .wrapper::before,
.gallery-3d-cards-section .wrapper::after {
  content: "";
  opacity: 0;
  width: 100%;
  height: 80px;
  transition: all 0.5s;
  position: absolute;
  left: 0;
}
.gallery-3d-cards-section .wrapper::before {
  top: 0;
  height: 100%;
  background-image: linear-gradient(
    to top,
    transparent 46%,
    rgba(12, 13, 19, 0.5) 68%,
    rgba(12, 13, 19) 97%
  );
}
.gallery-3d-cards-section .wrapper::after {
  bottom: 0;
  opacity: 1;
  background-image: linear-gradient(
    to bottom,
    transparent 46%,
    rgba(12, 13, 19, 0.5) 68%,
    rgba(12, 13, 19) 97%
  );
}

.gallery-3d-cards-section .card:hover .wrapper::before,
.gallery-3d-cards-section .card:hover .wrapper::after {
  opacity: 1;
}

.gallery-3d-cards-section .card:hover .wrapper::after {
  height: 120px;
}
.gallery-3d-cards-section .title {
  width: 100%;
  transition: transform 0.5s;
}
.gallery-3d-cards-section .card:hover .title {
  transform: translate3d(0%, -50px, 100px);
}

.gallery-3d-cards-section .character {
  width: 100%;
  opacity: 0;
  transition: all 0.5s;
  position: absolute;
  z-index: -1;
  bottom: 0;
}

.gallery-3d-cards-section .card:hover .character {
  opacity: 1;
  transform: translate3d(0%, -30%, 100px);
}
