css:
.gallery-4col {
    padding: 3rem 0;
    overflow-x: hidden;
    width: 100%;
    box-sizing: border-box;
}

.gallery-4col .container {
    width: 100%;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 1rem;
    box-sizing: border-box;
}

.gallery-4col .gallery-title {
    text-align: center;
    font-size: clamp(1.75rem, 5vw, 2.5rem);
    margin-bottom: 2rem;
    color: #111827;
    padding: 0 1rem;
}

.gallery-4col .gallery-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(min(100%, 220px), 1fr));
    gap: 1rem;
    width: 100%;
    box-sizing: border-box;
}

.gallery-4col .gallery-image {
    overflow: hidden;
    border-radius: 8px;
    aspect-ratio: 1 / 1;
    width: 100%;
    box-sizing: border-box;
}

.gallery-4col .gallery-image img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    display: block;
    transition: transform 0.3s ease;
}

@media (hover: hover) {
    .gallery-4col .gallery-image:hover img {
        transform: scale(1.05);
    }
}

@media (max-width: 767px) {
    .gallery-4col {
        padding: 2rem 0;
    }

    .gallery-4col .gallery-grid {
        grid-template-columns: repeat(2, 1fr);
        gap: 0.75rem;
    }
}

@media (min-width: 768px) and (max-width: 1023px) {
    .gallery-4col .gallery-grid {
        grid-template-columns: repeat(3, 1fr);
        gap: 1rem;
    }
}

@media (min-width: 1024px) {
    .gallery-4col .gallery-grid {
        grid-template-columns: repeat(4, 1fr);
        gap: 1.25rem;
    }
}
