css:
@keyframes cardFadeInUp {
    from {
        opacity: 0;
        transform: translateY(20px);
    }
    to {
        opacity: 1;
        transform: translateY(0);
    }
}

.card-reveal-gallery-container {
    width: 100%;
    padding: 2rem 1rem;
    box-sizing: border-box;
    font-family: 'Roboto', sans-serif;
    background-color: #E9ECEF;
}

.card-reveal-gallery-header {
    text-align: center;
    margin-bottom: 2.5rem;
}

.card-reveal-gallery-header h1 {
    font-size: 2.5rem;
    color: #212529;
    margin-bottom: 0.5rem;
}

.card-reveal-gallery-header p {
    font-size: 1.1rem;
    color: #495057;
    max-width: 600px;
    margin: 0 auto 1.5rem auto;
}

.card-reveal-gallery-cta {
    display: inline-block;
    padding: 0.75rem 1.5rem;
    background-color: #DC3545;
    color: #FFFFFF;
    border-radius: 5px;
    text-decoration: none;
    font-weight: bold;
    transition: background-color 0.3s ease;
}

.card-reveal-gallery-cta:hover {
    background-color: #C82333;
}

.card-reveal-gallery-grid {
    display: grid;
    grid-template-columns: 1fr;
    gap: 1.5rem;
}

.card-reveal-gallery-card {
    position: relative;
    border-radius: 8px;
    overflow: hidden;
    box-shadow: 0 8px 16px rgba(0,0,0,0.15);
    opacity: 0;
    transform: translateY(20px);
    animation: cardFadeInUp 0.6s ease-out forwards;
    animation-delay: var(--card-delay, 0s);
}

.card-reveal-gallery-card img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    display: block;
}

.card-reveal-gallery-caption {
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    background: linear-gradient(to top, rgba(0,0,0,0.8), transparent);
    color: #FFFFFF;
    padding: 2rem 1rem 1rem 1rem;
    transform: translateY(100%);
    transition: transform 0.4s ease-in-out;
}

.card-reveal-gallery-card:hover .card-reveal-gallery-caption {
    transform: translateY(0);
}

.card-reveal-gallery-caption span {
    font-size: 1.25rem;
    font-weight: bold;
}

@media (min-width: 768px) {
    .card-reveal-gallery-grid {
        grid-template-columns: repeat(2, 1fr);
    }
}

@media (min-width: 1024px) {
    .card-reveal-gallery-container {
        padding: 4rem 2rem;
    }
    .card-reveal-gallery-header h1 {
        font-size: 3rem;
    }
}

js:
window.addEventListener('load', () => {
  const pixabayImages = document.querySelectorAll('img[src^="https://pixabay.com/api/"]');
  pixabayImages.forEach(img => {
    const apiUrl = img.src;
    fetch(apiUrl)
      .then(response => response.json())
      .then(data => {
        if (data.hits && data.hits.length > 0) {
          img.src = data.hits[0].largeImageURL;
        } else {
          img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
        }
      })
      .catch(error => {
        console.error('Error fetching Pixabay image:', error);
        img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
      });
  });
});
