css:
.split-gallery-container {
    display: flex;
    flex-direction: column;
    width: 100%;
    min-height: 90vh;
    font-family: 'Montserrat', sans-serif;
    background-color: #111111;
}

.split-gallery-featured-panel {
    position: relative;
    width: 100%;
    height: 50vh;
    color: #FFFFFF;
}

.split-gallery-featured-panel img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    filter: brightness(0.6);
}

.split-gallery-content {
    position: absolute;
    bottom: 1rem;
    left: 1rem;
    padding: 1rem;
}

.split-gallery-content h1 {
    font-size: 2.5rem;
    margin: 0 0 0.5rem 0;
}

.split-gallery-content p {
    font-size: 1.1rem;
    margin: 0 0 1.5rem 0;
}

.split-gallery-cta {
    display: inline-block;
    padding: 0.7rem 1.8rem;
    border: 2px solid #FFFFFF;
    color: #FFFFFF;
    text-decoration: none;
    font-weight: bold;
    transition: background-color 0.3s, color 0.3s;
}

.split-gallery-cta:hover {
    background-color: #FFFFFF;
    color: #111111;
}

.split-gallery-thumbnail-panel {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 0.5rem;
    padding: 0.5rem;
    background-color: #111111;
    height: 40vh;
    overflow-y: auto;
}

.split-gallery-thumbnail {
    width: 100%;
    height: 150px;
    object-fit: cover;
    cursor: pointer;
    opacity: 0.7;
    transition: opacity 0.3s;
    border-radius: 4px;
}

.split-gallery-thumbnail:hover {
    opacity: 1;
}

@media (min-width: 768px) {
    .split-gallery-container {
        flex-direction: row;
    }
    .split-gallery-featured-panel {
        width: 65%;
        height: auto;
    }
    .split-gallery-thumbnail-panel {
        width: 35%;
        height: auto;
        grid-template-columns: repeat(2, 1fr);
        padding: 1rem;
        gap: 1rem;
    }
    .split-gallery-thumbnail {
        height: 180px;
    }
    .split-gallery-content {
        bottom: 2rem;
        left: 2rem;
    }
}

@media (min-width: 1024px) {
    .split-gallery-thumbnail-panel {
        grid-template-columns: 1fr;
    }
    .split-gallery-thumbnail {
        height: 150px;
    }
    .split-gallery-content h1 {
        font-size: 3.5rem;
    }
}

js:
document.addEventListener('DOMContentLoaded', () => {
    const featuredImg = document.getElementById('split-gallery-featured-img');
    const thumbnails = document.querySelectorAll('.split-gallery-thumbnail');

    thumbnails.forEach(thumb => {
        thumb.addEventListener('click', function() {
            featuredImg.src = this.src;
        });
    });
});

window.addEventListener('load', () => {
  const pixabayImages = document.querySelectorAll('img[src^="https://pixabay.com/api/"]');
  pixabayImages.forEach(img => {
    const apiUrl = img.src;
    fetch(apiUrl)
      .then(response => response.json())
      .then(data => {
        if (data.hits && data.hits.length > 0) {
          img.src = data.hits[0].largeImageURL;
        } else {
          img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
        }
      })
      .catch(error => {
        console.error('Error fetching Pixabay image:', error);
        img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
      });
  });
});
