css:
.gallery-circular-segments {
  width: 80vmin;
  aspect-ratio: 1;
  position: relative;
  margin: 4rem auto; 
  background: #000;
  background-size: cover;
  background-position: center center;
  overflow: clip;
  clip-path: circle(50%);
  -webkit-mask: 
    radial-gradient(farthest-side, #000 95%, #0000);
  border-radius: 50%;
}

.gallery-circular-segments img {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  width: 100%;
  height: 100%;
  object-fit: cover;
  object-position: center center;
  -webkit-mask: conic-gradient(from var(--i), #0000, #000 5deg 60deg, #0000 70deg);
  transition: object-position 1s, opacity 0.5s;
  transition-delay: 0s, 0s;
  cursor: pointer;
}

.gallery-circular-segments img:nth-child(1) { 
  --i: -35deg; 
  object-position: 50% -50vmin; 
  clip-path: polygon(50% 50%, 85% 0, 15% 0);
}

.gallery-circular-segments img:nth-child(2) { 
  --i:  25deg;
  object-position: 15vmin 100%; 
  clip-path: polygon(50% 50%, 100% 60%, 100% 0, 70% 0);
}
.gallery-circular-segments img:nth-child(3) {
  --i:  85deg;
  object-position: 28vmin 10vmin; 
  clip-path: polygon(50% 50%, 100% 40%, 100% 100%, 70% 100%);
}
.gallery-circular-segments img:nth-child(4) { 
  --i: 145deg;
  object-position: 20% 20vmin; 
  clip-path: polygon(50% 50%, 90% 100%, 10% 100%);
}
.gallery-circular-segments img:nth-child(5) { 
  --i: 205deg;
  object-position: -15vmin -20%; 
  clip-path: polygon(50% 50%, 0 40%, 0 100%, 30% 100%);
}
.gallery-circular-segments img:nth-child(6) { 
  --i: 265deg;
  object-position: -30vmin -10vmin; 
  clip-path: polygon(50% 50%, 0 60%, 0 0, 30% 0);
}

.gallery-circular-segments:focus-within img,
.gallery-circular-segments:hover img {
  opacity: 0.25;
}

.gallery-circular-segments img:focus,
.gallery-circular-segments img:hover {
  opacity: 1;
  object-position: 50% 50%;
  transition-delay: 0.5s, 0s;
}
