.slider-gallery-container {
  position: relative;
  width: 100%;
  height: 100vh;
  overflow: hidden;
  font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
}

.slider-gallery-wrapper {
  display: flex;
  width: 100%;
  height: 100%;
  transition: transform 0.5s ease-in-out;
}

.slider-gallery-slide {
  min-width: 100%;
  height: 100%;
  position: relative;
  opacity: 0;
  transition: opacity 0.5s ease-in-out;
  visibility: hidden;
}

.slider-gallery-slide.active {
    opacity: 1;
    visibility: visible;
    position: absolute;
    top: 0;
    left: 0;
}

.slider-gallery-slide img {
  width: 100%;
  height: 100%;
  object-fit: cover;
}

.slider-gallery-content {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  color: #FFFFFF;
  text-align: center;
  background-color: rgba(0, 0, 0, 0.5);
  padding: 1.5rem;
  border-radius: 8px;
  width: 90%;
  max-width: 600px;
}

.slider-gallery-content h1 {
  font-size: 2rem;
  margin-bottom: 0.5rem;
}

.slider-gallery-content p {
  font-size: 1rem;
  margin-bottom: 1.5rem;
}

.slider-gallery-cta {
  display: inline-block;
  padding: 0.75rem 1.5rem;
  background-color: #17A2B8;
  color: #FFFFFF;
  border-radius: 5px;
  text-decoration: none;
  font-weight: bold;
  transition: background-color 0.3s ease;
}

.slider-gallery-cta:hover {
  background-color: #117a8b;
}

.slider-gallery-nav {
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background-color: rgba(0, 0, 0, 0.5);
  color: #FFFFFF;
  border: none;
  font-size: 1.5rem;
  padding: 0.75rem;
  cursor: pointer;
  z-index: 10;
  border-radius: 50%;
  width: 50px;
  height: 50px;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background-color 0.3s ease;
}

.slider-gallery-nav:hover {
    background-color: rgba(0, 0, 0, 0.8);
}

.slider-gallery-nav.prev {
  left: 1rem;
}

.slider-gallery-nav.next {
  right: 1rem;
}

.slider-gallery-dots {
  position: absolute;
  bottom: 1.5rem;
  left: 50%;
  transform: translateX(-50%);
  display: flex;
  z-index: 10;
}

.slider-gallery-dot {
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background-color: rgba(255, 255, 255, 0.5);
  margin: 0 5px;
  cursor: pointer;
  transition: background-color 0.3s ease;
}

.slider-gallery-dot.active {
  background-color: #FFFFFF;
}

@media (min-width: 768px) {
    .slider-gallery-content h1 {
      font-size: 3rem;
    }
    .slider-gallery-content p {
      font-size: 1.25rem;
    }
}

js:
document.addEventListener('DOMContentLoaded', () => {
  const slides = document.querySelectorAll('.slider-gallery-slide');
  const nextBtn = document.querySelector('.slider-gallery-nav.next');
  const prevBtn = document.querySelector('.slider-gallery-nav.prev');
  const dotsContainer = document.querySelector('.slider-gallery-dots');
  let currentIndex = 0;

  function goToSlide(index) {
    slides.forEach((slide, i) => {
      slide.classList.remove('active');
      if (i === index) {
        slide.classList.add('active');
      }
    });
    updateDots(index);
    currentIndex = index;
  }

  function createDots() {
    slides.forEach((_, i) => {
        const dot = document.createElement('div');
        dot.classList.add('slider-gallery-dot');
        dot.addEventListener('click', () => goToSlide(i));
        dotsContainer.appendChild(dot);
    });
  }

  function updateDots(index) {
      const dots = document.querySelectorAll('.slider-gallery-dot');
      dots.forEach((dot, i) => {
          dot.classList.toggle('active', i === index);
      });
  }

  nextBtn.addEventListener('click', () => {
    const nextIndex = (currentIndex + 1) % slides.length;
    goToSlide(nextIndex);
  });

  prevBtn.addEventListener('click', () => {
    const prevIndex = (currentIndex - 1 + slides.length) % slides.length;
    goToSlide(prevIndex);
  });

  createDots();
  goToSlide(0);
});

window.addEventListener('load', () => {
  const pixabayImages = document.querySelectorAll('img[src^="https://pixabay.com/api/"]');
  pixabayImages.forEach(img => {
    const apiUrl = img.src;
    fetch(apiUrl)
      .then(response => response.json())
      .then(data => {
        if (data.hits && data.hits.length > 0) {
          img.src = data.hits[0].largeImageURL;
        } else {
          img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
        }
      })
      .catch(error => {
        console.error('Error fetching Pixabay image:', error);
        img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
      });
  });
});
