css:
.modal-gallery-container {
    width: 100%;
    padding: 2rem 1rem;
    box-sizing: border-box;
    font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
    background-color: #FFFFFF;
}

.modal-gallery-header {
    text-align: center;
    margin-bottom: 2.5rem;
}

.modal-gallery-header h1 {
    font-size: 2.5rem;
    color: #343A40;
    margin-bottom: 0.5rem;
}

.modal-gallery-header p {
    font-size: 1.1rem;
    color: #6C757D;
    max-width: 600px;
    margin: 0 auto 1.5rem auto;
}

.modal-gallery-cta {
    display: inline-block;
    padding: 0.75rem 1.5rem;
    background-color: #28A745;
    color: #FFFFFF;
    border-radius: 5px;
    text-decoration: none;
    font-weight: bold;
    transition: background-color 0.3s ease;
}

.modal-gallery-cta:hover {
    background-color: #218838;
}

.modal-gallery-grid {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    gap: 1rem;
}

.modal-gallery-item {
    overflow: hidden;
    border-radius: 8px;
    cursor: pointer;
}

.modal-gallery-item img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    display: block;
    transition: transform 0.3s ease;
}

.modal-gallery-item:hover img {
    transform: scale(1.05);
}

.modal-gallery-preview-container {
    display: none;
    position: fixed;
    z-index: 1000;
    padding-top: 60px;
    left: 0;
    top: 0;
    width: 100%;
    height: 100%;
    overflow: auto;
    background-color: rgba(0,0,0,0.9);
}

.modal-gallery-preview-content {
    margin: auto;
    display: block;
    width: 80%;
    max-width: 1200px;
    animation: zoomIn 0.3s ease;
}

@keyframes zoomIn {
    from {transform: scale(0)} 
    to {transform: scale(1)}
}

.modal-gallery-close {
    position: absolute;
    top: 15px;
    right: 35px;
    color: #F1F1F1;
    font-size: 40px;
    font-weight: bold;
    transition: 0.3s;
    cursor: pointer;
}

.modal-gallery-close:hover, .modal-gallery-close:focus {
    color: #BBB;
    text-decoration: none;
}

@media (min-width: 768px) {
    .modal-gallery-grid {
        grid-template-columns: repeat(3, 1fr);
    }
}

@media (min-width: 1024px) {
    .modal-gallery-container {
        padding: 4rem 2rem;
    }
    .modal-gallery-header h1 {
        font-size: 3rem;
    }
}

js:
document.addEventListener('DOMContentLoaded', () => {
    const modal = document.getElementById('modal-gallery-preview');
    const modalImg = document.getElementById('modal-gallery-img');
    const galleryItems = document.querySelectorAll('.modal-gallery-item img');
    const closeBtn = document.querySelector('.modal-gallery-close');

    galleryItems.forEach(item => {
        item.addEventListener('click', function() {
            modal.style.display = 'block';
            modalImg.src = this.src; // Uses the already fetched high-res URL
        });
    });

    function closeModal() {
        modal.style.display = 'none';
    }

    closeBtn.addEventListener('click', closeModal);

    window.addEventListener('click', function(event) {
        if (event.target == modal) {
            closeModal();
        }
    });
});

window.addEventListener('load', () => {
  const pixabayImages = document.querySelectorAll('img[src^="https://pixabay.com/api/"]');
  pixabayImages.forEach(img => {
    const apiUrl = img.src;
    fetch(apiUrl)
      .then(response => response.json())
      .then(data => {
        if (data.hits && data.hits.length > 0) {
          img.src = data.hits[0].largeImageURL;
        } else {
          img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
        }
      })
      .catch(error => {
        console.error('Error fetching Pixabay image:', error);
        img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
      });
  });
});
