css:
.filmstrip-gallery-section {
    width: 100%;
    padding: 2rem 0;
    background-color: #F4F4F4;
    font-family: 'Lato', sans-serif;
}

.filmstrip-gallery-header {
    text-align: center;
    margin: 0 1rem 2.5rem 1rem;
}

.filmstrip-gallery-header h1 {
    font-size: 2.5rem;
    color: #333333;
    margin-bottom: 0.5rem;
}

.filmstrip-gallery-header p {
    font-size: 1.1rem;
    color: #555555;
    max-width: 600px;
    margin: 0 auto 1.5rem auto;
}

.filmstrip-gallery-cta {
    display: inline-block;
    padding: 0.75rem 1.5rem;
    background-color: #333333;
    color: #FFFFFF;
    border-radius: 30px;
    text-decoration: none;
    font-weight: bold;
    transition: background-color 0.3s ease;
}

.filmstrip-gallery-cta:hover {
    background-color: #555555;
}

.filmstrip-gallery-container {
    overflow-x: auto;
    -webkit-overflow-scrolling: touch;
    padding: 0 1rem;
}

.filmstrip-gallery-container::-webkit-scrollbar {
    display: none;
}

.filmstrip-gallery-wrapper {
    display: flex;
    flex-wrap: nowrap;
    gap: 1rem;
    padding-bottom: 1rem; /* for shadow visibility */
}

.filmstrip-gallery-item {
    flex: 0 0 70%; /* Each item takes 70% of viewport width */
    border-radius: 12px;
    overflow: hidden;
    box-shadow: 0 5px 15px rgba(0,0,0,0.1);
}

.filmstrip-gallery-item img {
    width: 100%;
    height: 300px;
    object-fit: cover;
    display: block;
}

@media (min-width: 768px) {
    .filmstrip-gallery-item {
        flex: 0 0 40%;
    }
    .filmstrip-gallery-item img {
        height: 400px;
    }
    .filmstrip-gallery-container {
        padding: 0 2rem;
    }
}

@media (min-width: 1024px) {
    .filmstrip-gallery-section {
        padding: 4rem 0;
    }
    .filmstrip-gallery-item {
        flex: 0 0 30%;
    }
    .filmstrip-gallery-item img {
        height: 450px;
    }
    .filmstrip-gallery-container {
        padding: 0 4rem;
    }
}

js:
window.addEventListener('load', () => {
  const pixabayImages = document.querySelectorAll('img[src^="https://pixabay.com/api/"]');
  pixabayImages.forEach(img => {
    const apiUrl = img.src;
    fetch(apiUrl)
      .then(response => response.json())
      .then(data => {
        if (data.hits && data.hits.length > 0) {
          img.src = data.hits[0].largeImageURL;
        } else {
          img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
        }
      })
      .catch(error => {
        console.error('Error fetching Pixabay image:', error);
        img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
      });
  });
});
