css:
.gallery-masonry {
    padding: 3rem 0;
    overflow-x: hidden;
    width: 100%;
    box-sizing: border-box;
}

.gallery-masonry .container {
    width: 100%;
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 1rem;
    box-sizing: border-box;
}

.gallery-masonry .text-center {
    text-align: center;
}

.gallery-masonry .section-headline {
    font-size: clamp(1.75rem, 5vw, 2.5rem);
    font-weight: 700;
    margin-bottom: 2rem;
    color: #111827;
    padding: 0 1rem;
}

.gallery-masonry .masonry-grid {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    grid-auto-rows: 180px;
    gap: 1rem;
    width: 100%;
    box-sizing: border-box;
}

.gallery-masonry .gallery-item {
    overflow: hidden;
    border-radius: 12px;
    position: relative;
    cursor: pointer;
    width: 100%;
    box-sizing: border-box;
}

.gallery-masonry .gallery-item img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    display: block;
    transition: transform 0.4s ease;
}

@media (hover: hover) {
    .gallery-masonry .gallery-item:hover img {
        transform: scale(1.08);
    }
}

.gallery-masonry .item-large { 
    grid-column: span 2; 
    grid-row: span 2; 
}

.gallery-masonry .item-medium-v { 
    grid-row: span 2; 
}

.gallery-masonry .item-medium-h { 
    grid-column: span 2; 
}

.gallery-masonry .item-small { 
    grid-column: span 1; 
    grid-row: span 1; 
}

/* الأجهزة الصغيرة */
@media (max-width: 767px) {
    .gallery-masonry {
        padding: 2rem 0;
    }

    .gallery-masonry .masonry-grid {
        grid-template-columns: 1fr;
        grid-auto-rows: 220px;
        gap: 0.75rem;
    }

    .gallery-masonry .item-large, 
    .gallery-masonry .item-medium-v, 
    .gallery-masonry .item-medium-h, 
    .gallery-masonry .item-small {
        grid-column: span 1;
        grid-row: span 1;
    }
}

/* التابلت */
@media (min-width: 768px) and (max-width: 1023px) {
    .gallery-masonry .masonry-grid {
        grid-template-columns: repeat(2, 1fr);
        grid-auto-rows: 200px;
        gap: 1rem;
    }

    .gallery-masonry .item-large { 
        grid-column: span 2; 
        grid-row: span 2; 
    }

    .gallery-masonry .item-medium-v,
    .gallery-masonry .item-medium-h,
    .gallery-masonry .item-small {
        grid-column: span 1;
        grid-row: span 1;
    }
}
