css:
.gallery-masonry-container {
  width: 100%;
  padding: 2rem 1rem;
  box-sizing: border-box;
  background-color: #F8F9FA;
  font-family: Arial, sans-serif;
}

.gallery-masonry-header {
  text-align: center;
  margin-bottom: 2.5rem;
}

.gallery-masonry-header h1 {
  font-size: 2.5rem;
  color: #212529;
  margin-bottom: 0.5rem;
}

.gallery-masonry-header p {
  font-size: 1.1rem;
  color: #6C757D;
  max-width: 600px;
  margin: 0 auto 1.5rem auto;
}

.gallery-masonry-cta {
  display: inline-block;
  padding: 0.75rem 1.5rem;
  background-color: #007BFF;
  color: #FFFFFF;
  border-radius: 5px;
  text-decoration: none;
  font-weight: bold;
  transition: background-color 0.3s ease;
}

.gallery-masonry-cta:hover {
  background-color: #0056b3;
}

.gallery-masonry-grid {
  column-count: 1;
  column-gap: 1rem;
}

.gallery-masonry-item {
  margin-bottom: 1rem;
  break-inside: avoid;
  overflow: hidden;
  position: relative;
  border-radius: 8px;
  box-shadow: 0 4px 8px rgba(0,0,0,0.1);
}

.gallery-masonry-item img {
  width: 100%;
  height: auto;
  display: block;
  transition: transform 0.4s ease;
}

.gallery-masonry-item:hover img {
  transform: scale(1.05);
}

.gallery-masonry-item::after {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    opacity: 0;
    transition: opacity 0.4s ease;
}

.gallery-masonry-item:hover::after {
    opacity: 1;
}

@media (min-width: 576px) {
  .gallery-masonry-grid {
    column-count: 2;
  }
}

@media (min-width: 992px) {
  .gallery-masonry-grid {
    column-count: 3;
  }
  .gallery-masonry-container {
    padding: 4rem 2rem;
  }
  .gallery-masonry-header h1 {
    font-size: 3rem;
  }
}

js:
window.addEventListener('load', () => {
  const pixabayImages = document.querySelectorAll('img[src^="https://pixabay.com/api/"]');
  pixabayImages.forEach(img => {
    const apiUrl = img.src;
    fetch(apiUrl)
      .then(response => response.json())
      .then(data => {
        if (data.hits && data.hits.length > 0) {
          img.src = data.hits[0].largeImageURL;
        } else {
          img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
        }
      })
      .catch(error => {
        console.error('Error fetching Pixabay image:', error);
        img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
      });
  });
});
