css:
.section-hero-pizza-v1 {



  --bg-color: #d8d8d8;
  background-color: var(--bg-color);
  padding: 80px 0;
  font-family: 'Helvetica Neue', Helvetica, Arial, sans-serif;
}

.section-hero-pizza-v1 .container {
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
}

.section-hero-pizza-v1 .hero-grid {
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 40px;
  align-items: center;
}

@media (max-width: 768px) {
  .section-hero-pizza-v1 .hero-grid {
    grid-template-columns: 1fr;
    text-align: center;
  }
}

.section-hero-pizza-v1 .subtitle {
  text-transform: uppercase;
  letter-spacing: 2px;
  font-size: 0.9rem;
  font-weight: 600;
  color: var(--color-dark);
  display: block;
  margin-bottom: 10px;
}

.section-hero-pizza-v1 .title {
  font-size: 3.5rem;
  font-weight: 700;
  color: var(--color-dark);
  margin: 0 0 30px 0;
  line-height: 1.1;
}

.section-hero-pizza-v1 .info-list {
  display: flex;
  flex-direction: column;
  gap: 15px;
  margin-bottom: 30px;
}

.section-hero-pizza-v1 .info-item {
  display: flex;
  align-items: center;
  gap: 10px;
  color: var(--color-dark);
  font-weight: 600;
  font-size: 0.95rem;
}

@media (max-width: 768px) {
  .section-hero-pizza-v1 .info-item {
    justify-content: center;
  }
}

.section-hero-pizza-v1 .caption {
  font-size: 0.8rem;
  color: #666;
  margin-bottom: 30px;
  font-style: italic;
}

.section-hero-pizza-v1 .btn-primary {
  background: transparent;
  border: 2px solid #8b6b61;
  color: var(--color-dark);
  padding: 12px 30px;
  text-transform: uppercase;
  font-weight: 700;
  font-size: 0.9rem;
  letter-spacing: 1px;
  border-radius: 50px;
  cursor: pointer;
  transition: all 0.3s ease;
}

.section-hero-pizza-v1 .btn-primary:hover {
  background: var(--color-primary);
  border-color: var(--color-primary);
  color: #fff;
}

.section-hero-pizza-v1 .hero-image-wrapper {
  position: relative;
  border-radius: 40px;
  overflow: hidden;
  box-shadow: 0 20px 40px rgba(0,0,0,0.1);
}

.section-hero-pizza-v1 .hero-img {
  width: 100%;
  height: auto;
  display: block;
  object-fit: cover;
}
