css:
.section-happiness-collage {
  --color-bg: #fffaf9;
  --color-text: #333333;
  --color-pink-accent: #ef7d8d;
  --color-yellow-accent: #f4c542;


  padding: 4rem 0;
  background-color: #ffffff;
}

.section-happiness-collage .container {
  max-width: 1200px;
  margin: 0 auto;
}

.section-happiness-collage .collage-wrapper {
  display: grid;
  grid-template-columns: 1fr 1fr;
  grid-template-rows: 400px 400px;
  position: relative;
  gap: 0;
}

.section-happiness-collage .img-box {
  width: 100%;
  height: 100%;
  overflow: hidden;
  position: relative;
}

.section-happiness-collage .bg-img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  display: block;
}

/* Center Floating Card */
.section-happiness-collage .content-card {
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
  z-index: 10;
  background-color: var(--color-bg);
  padding: 3rem 4rem;
  text-align: center;
  max-width: 500px;
  width: 90%;
  box-shadow: 0 10px 30px rgba(0,0,0,0.05);
}

.section-happiness-collage .card-title {
  font-family: var(--font-heading);
  font-size: 2rem;
  color: var(--color-text);
  margin-bottom: 1rem;
  font-weight: 500;
}

.section-happiness-collage .card-text {
  font-family: var(--font-body);
  color: #666;
  line-height: 1.6;
  margin-bottom: 2rem;
  font-size: 1rem;
}

.section-happiness-collage .btn-learn {
  display: inline-block;
  padding: 0.8rem 2rem;
  border: 1px solid #333;
  text-decoration: none;
  color: #333;
  text-transform: lowercase;
  font-family: var(--font-heading);
  font-size: 1.1rem;
  transition: all 0.3s ease;
  background: transparent;
}

.section-happiness-collage .btn-learn:hover {
  background-color: #333;
  color: #fff;
}

.section-happiness-collage .card-footer {
  margin-top: 2rem;
  font-size: 0.85rem;
  color: #999;
  font-family: var(--font-body);
}

.section-happiness-collage .card-footer a {
  color: #666;
  text-decoration: underline;
}

@media (max-width: 768px) {
  .section-happiness-collage .collage-wrapper {
    grid-template-columns: 1fr;
    grid-template-rows: auto;
    display: flex;
    flex-direction: column;
  }

  .section-happiness-collage .img-box {
    height: 300px;
  }

  .section-happiness-collage .content-card {
    position: relative;
    transform: none;
    top: auto;
    left: auto;
    width: 100%;
    max-width: 100%;
    padding: 3rem 1.5rem;
    order: -1; /* Move to top on mobile for visibility */
  }
}
