css:
.layout-section {
    padding: 2rem 0;
}
.layout-section .container {
    display: grid;
    gap: 1.5rem;
    width: 90%;
    max-width: 1200px;
    margin: 0 auto;
}
.layout-section .layout-placeholder {
    background-color: #f3f4f6; /* gray-100 */
    border: 2px dashed #d1d5db; /* gray-300 */
    border-radius: 0.75rem;
    min-height: 150px;
    display: flex;
    align-items: center;
    justify-content: center;
    color: #9ca3af; /* gray-400 */
    font-family: sans-serif;
    font-size: 0.9rem;
    transition: all 0.2s ease-in-out;
    position: relative;
}
.layout-section .layout-placeholder:hover {
    background-color: #e5e7eb; /* gray-200 */
    border-color: #9ca3af; /* gray-400 */
    cursor: pointer;
}
.layout-section .layout-placeholder::before {
    content: 'Click to add component';
    position: absolute;
}
.layout-section .layout-placeholder:not(:empty)::before {
    display: none;
}
