css:
.nav-accent-border { font-family: sans-serif; position: relative; background-color: var(--color-light, #FFFFFF); padding: 1.5rem 0; }
.nav-accent-border .nav-container { display: flex; justify-content: space-between; align-items: center; max-width: 1200px; margin: 0 auto; padding: 0 1rem; }
.nav-accent-border .logo { font-size: 1.5rem; font-weight: 700; color: var(--color-dark, #111827); text-decoration: none; }
.nav-accent-border .nav-links { display: none; gap: 2rem; }
.nav-accent-border .nav-links a { color: var(--color-secondary, #6B7280); font-weight: 500; text-decoration: none; padding-bottom: 0.5rem; position: relative; }
.nav-accent-border .nav-links a::after { content: ''; position: absolute; bottom: 0; left: 0; right: 0; height: 2px; background-color: var(--color-primary, #4B88D7); transform: scaleX(0); transition: transform 0.3s ease; }
.nav-accent-border .nav-links a:hover::after, .nav-accent-border .nav-links a.active::after { transform: scaleX(1); }
.nav-accent-border .nav-links a.active { color: var(--color-dark, #111827); }
.nav-accent-border .mobile-menu-toggle { display: block; background: none; border: none; cursor: pointer; }
.nav-accent-border .mobile-menu-toggle span { display: block; width: 25px; height: 2px; background-color: var(--color-dark, #111827); margin: 5px 0; }
.nav-accent-border .mobile-menu { display: none; flex-direction: column; gap: 1rem; padding: 1rem; background-color: var(--color-light, #FFFFFF); position: absolute; top: 100%; left: 0; right: 0; z-index: 99; text-align: center; }
.nav-accent-border.open .mobile-menu { display: flex; }
@media(min-width: 768px) { .nav-accent-border .nav-links { display: flex; } .nav-accent-border .mobile-menu-toggle { display: none; } }
