css:
.nav-auth-white { background-color: var(--color-light, #FFFFFF); padding: 1rem 0; position: relative; }
.nav-auth-white .nav-container { max-width: 1200px; margin: 0 auto; padding: 0 1rem; display: flex; justify-content: space-between; align-items: center; }
.nav-auth-white .logo { font-size: 2rem; font-weight: 700; color: var(--color-dark, #1F2937); text-decoration: none; }
.nav-auth-white .nav-links { display: none; gap: 1.5rem; }
@media (min-width: 992px) { .nav-auth-white .nav-links { display: flex; } }
.nav-auth-white .nav-links a { text-decoration: none; color: var(--color-dark, #1F2937); font-weight: 500; }
.nav-auth-white .auth-buttons { display: none; gap: 1rem; }
@media (min-width: 992px) { .nav-auth-white .auth-buttons { display: flex; } }
.nav-auth-white .btn-outline { background-color: transparent; border: 1px solid #E2E8F0; color: var(--color-dark, #1F2937); padding: 0.6rem 1.2rem; border-radius: 0.5rem; text-decoration: none; font-weight: 600; }
.nav-auth-white .btn-solid { background-color: #2D3748; color: white; padding: 0.6rem 1.2rem; border-radius: 0.5rem; text-decoration: none; font-weight: 600; }
.nav-auth-white .mobile-menu-toggle { display: block; background: none; border: none; cursor: pointer; z-index: 100; }
@media (min-width: 992px) { .nav-auth-white .mobile-menu-toggle { display: none; } }
.nav-auth-white .mobile-menu-toggle span { display: block; width: 25px; height: 2px; background-color: var(--color-dark, #1F2937); margin: 5px 0; transition: all 0.3s; }
.nav-auth-white .mobile-menu { display: none; flex-direction: column; gap: 1.5rem; padding: 6rem 2rem 2rem; background-color: var(--color-light, #FFFFFF); position: absolute; top: 0; left: 0; right: 0; text-align: center; z-index: 99; }
.nav-auth-white .auth-buttons-mobile { display: flex; flex-direction: column; gap: 1rem; margin-top: 1rem; }
.nav-auth-white.open .mobile-menu { display: flex; }
.nav-auth-white.open .mobile-menu-toggle span:nth-child(1) { transform: rotate(45deg) translate(5px, 5px); }
.nav-auth-white.open .mobile-menu-toggle span:nth-child(2) { opacity: 0; }
.nav-auth-white.open .mobile-menu-toggle span:nth-child(3) { transform: rotate(-45deg) translate(5px, -5px); }
