css:
.nav-bold-logotype { font-family: sans-serif; position: relative; background-color: var(--color-light, #FFFFFF); padding: 1.5rem 0; }
.nav-bold-logotype .nav-container { display: flex; justify-content: space-between; align-items: center; max-width: 1200px; margin: 0 auto; padding: 0 1.5rem; }
.nav-bold-logotype .logo { font-size: 2.5rem; font-weight: 900; color: var(--color-dark, #111827); text-decoration: none; letter-spacing: -2px; }
.nav-bold-logotype .nav-links { display: none; gap: 2.5rem; }
.nav-bold-logotype .nav-links a { color: var(--color-secondary, #6B7280); font-weight: 600; text-decoration: none; font-size: 1rem; }
.nav-bold-logotype .mobile-menu-toggle { display: block; background: none; border: none; cursor: pointer; }
.nav-bold-logotype .mobile-menu-toggle span { display: block; width: 25px; height: 3px; background-color: var(--color-dark, #111827); margin: 5px 0; }
.nav-bold-logotype .mobile-menu { display: none; flex-direction: column; gap: 1.5rem; padding: 2rem; background-color: var(--color-light, #FFFFFF); position: absolute; top: 100%; left: 0; right: 0; z-index: 99; text-align: center; }
.nav-bold-logotype.open .mobile-menu { display: flex; }
@media(min-width: 768px) { .nav-bold-logotype .nav-links { display: flex; } .nav-bold-logotype .mobile-menu-toggle { display: none; } }
