css:
.nav-button-group { font-family: sans-serif; position: relative; background-color: var(--color-light, #FFFFFF); padding: 1rem 0; border-bottom: 1px solid #e5e7eb; }
.nav-button-group .nav-container { display: flex; justify-content: space-between; align-items: center; max-width: 1200px; margin: 0 auto; padding: 0 1rem; }
.nav-button-group .logo { font-size: 1.5rem; font-weight: 700; color: var(--color-dark, #111827); text-decoration: none; }
.nav-button-group .nav-links { display: none; background-color: #f3f4f6; border-radius: 8px; border: 1px solid #e5e7eb; }
.nav-button-group .nav-links a { color: var(--color-secondary, #6B7280); font-weight: 500; text-decoration: none; padding: 0.6rem 1rem; border-right: 1px solid #e5e7eb; transition: all 0.3s; }
.nav-button-group .nav-links a:last-child { border-right: none; }
.nav-button-group .nav-links a.active { background-color: var(--color-light, #FFFFFF); color: var(--color-dark, #111827); }
.nav-button-group .mobile-menu-toggle { display: block; background: none; border: none; cursor: pointer; }
.nav-button-group .mobile-menu-toggle span { display: block; width: 25px; height: 2px; background-color: var(--color-dark, #111827); margin: 5px 0; }
.nav-button-group .mobile-menu { display: none; flex-direction: column; gap: 1rem; padding: 1rem; background-color: var(--color-light, #FFFFFF); position: absolute; top: 100%; left: 0; right: 0; z-index: 99; text-align: center; }
.nav-button-group.open .mobile-menu { display: flex; }
@media(min-width: 768px) { .nav-button-group .nav-links { display: flex; } .nav-button-group .mobile-menu-toggle { display: none; } }
