css:
.nav-centered-logo {
    background-color: #f0f2f5;
    padding: 1rem 0;
    position: relative;
}
.nav-centered-logo .nav-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 1rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
}
.nav-centered-logo .logo {
    font-size: 2rem;
    font-weight: 700;
    color: var(--color-dark, #1F2937);
    text-decoration: none;
    position: absolute;
    left: 50%;
    transform: translateX(-50%);
}
.nav-centered-logo .nav-links {
    list-style: none;
    display: none;
    gap: 1.5rem;
}
@media (min-width: 768px) {
    .nav-centered-logo .nav-links { display: flex; }
}
.nav-centered-logo .nav-links a {
    text-decoration: none;
    color: var(--color-dark, #1F2937);
    font-weight: 500;
}
.nav-centered-logo .mobile-menu-toggle {
    display: block;
    background: none;
    border: none;
    cursor: pointer;
    z-index: 100;
}
.nav-centered-logo .mobile-menu-toggle span { display: block; width: 25px; height: 2px; background-color: var(--color-dark, #1F2937); margin: 5px 0; transition: all 0.3s; }
@media (min-width: 768px) {
    .nav-centered-logo .mobile-menu-toggle { display: none; }
}
.nav-centered-logo .mobile-menu {
    display: none;
    flex-direction: column;
    gap: 1.5rem;
    padding: 2rem;
    background-color: #f0f2f5;
    position: absolute;
    top: 100%;
    left: 0;
    right: 0;
    text-align: center;
    z-index: 99;
}
.nav-centered-logo .mobile-menu a { text-decoration: none; color: var(--color-dark, #1F2937); font-weight: 500; font-size: 1.2rem; }
.nav-centered-logo.open .mobile-menu { display: flex; }
.nav-centered-logo.open .mobile-menu-toggle span:nth-child(1) { transform: rotate(45deg) translate(5px, 5px); }
.nav-centered-logo.open .mobile-menu-toggle span:nth-child(2) { opacity: 0; }
.nav-centered-logo.open .mobile-menu-toggle span:nth-child(3) { transform: rotate(-45deg) translate(5px, -5px); }
