css:
.nav-centered-tagline { font-family: sans-serif; background-color: var(--color-light, #FFFFFF); padding: 2rem 0; border-bottom: 1px solid #e5e7eb; }
.nav-centered-tagline .nav-container { text-align: center; max-width: 1200px; margin: 0 auto; padding: 0 1rem; }
.nav-centered-tagline .logo { font-size: 2rem; font-weight: 800; color: var(--color-dark, #111827); text-decoration: none; display: block; }
.nav-centered-tagline .tagline { font-size: 0.9rem; color: var(--color-secondary, #6B7280); margin-top: 0.25rem; letter-spacing: 0.5px; }
