css:
.nav-contact-strip { font-family: sans-serif; position: relative; }
.nav-contact-strip .nav-container { display: flex; justify-content: space-between; align-items: center; max-width: 1200px; margin: 0 auto; padding: 0 1rem; }
.nav-contact-strip .top-bar { background-color: var(--color-dark, #111827); color: var(--color-light, #FFFFFF); padding: 0.5rem 0; font-size: 0.8rem; }
.nav-contact-strip .top-bar .contact-info, .nav-contact-strip .top-bar .social-links { display: flex; gap: 1.5rem; }
.nav-contact-strip .top-bar a { color: var(--color-secondary, #9CA3AF); text-decoration: none; transition: color 0.3s; }
.nav-contact-strip .top-bar a:hover { color: var(--color-light, #FFFFFF); }
.nav-contact-strip .top-bar .contact-info i { margin-right: 0.5rem; }
.nav-contact-strip .main-nav { background-color: var(--color-light, #FFFFFF); padding: 1rem 0; border-bottom: 1px solid #e5e7eb; }
.nav-contact-strip .main-nav .logo { font-size: 1.5rem; font-weight: 700; color: var(--color-dark, #111827); text-decoration: none; }
.nav-contact-strip .main-nav .nav-links { display: none; gap: 2rem; }
.nav-contact-strip .main-nav .nav-links a { color: var(--color-dark, #111827); font-weight: 500; text-decoration: none; }
.nav-contact-strip .mobile-menu-toggle { display: block; background: none; border: none; cursor: pointer; }
.nav-contact-strip .mobile-menu-toggle span { display: block; width: 25px; height: 2px; background-color: var(--color-dark, #111827); margin: 5px 0; transition: all 0.3s; }
.nav-contact-strip .mobile-menu { display: none; flex-direction: column; gap: 1rem; padding: 1rem; background-color: var(--color-light, #FFFFFF); position: absolute; top: 100%; left: 0; right: 0; z-index: 99; border-bottom: 1px solid #e5e7eb; text-align: center; }
.nav-contact-strip.open .mobile-menu { display: flex; }
@media(min-width: 768px) { .nav-contact-strip .top-bar .contact-info, .nav-contact-strip .top-bar .social-links { display: flex; } .nav-contact-strip .main-nav .nav-links { display: flex; } .nav-contact-strip .mobile-menu-toggle { display: none; } }
