.nav-glassmorphism { font-family: sans-serif; position: relative; padding: 1rem 0; background: rgba(20, 20, 30, 0.5); backdrop-filter: blur(10px); -webkit-backdrop-filter: blur(10px); border-bottom: 1px solid rgba(255, 255, 255, 0.1); }
.nav-glassmorphism .nav-container { display: flex; justify-content: space-between; align-items: center; max-width: 1200px; margin: 0 auto; padding: 0 1rem; }
.nav-glassmorphism .logo { font-size: 1.5rem; font-weight: 700; color: var(--color-light, #FFFFFF); text-decoration: none; }
.nav-glassmorphism .nav-links { display: none; gap: 2rem; }
.nav-glassmorphism .nav-links a { color: var(--color-secondary, #9CA3AF); font-weight: 500; text-decoration: none; transition: color 0.3s; }
.nav-glassmorphism .nav-links a:hover { color: var(--color-light, #FFFFFF); }
.nav-glassmorphism .cta-button { display: none; padding: 0.6rem 1.2rem; background-color: rgba(255, 255, 255, 0.1); color: var(--color-light, #FFFFFF); border-radius: 6px; text-decoration: none; font-weight: 500; border: 1px solid rgba(255, 255, 255, 0.2); }
.nav-glassmorphism .mobile-menu-toggle { display: block; background: none; border: none; cursor: pointer; }
.nav-glassmorphism .mobile-menu-toggle span { display: block; width: 25px; height: 2px; background-color: var(--color-light, #FFFFFF); margin: 5px 0; }
.nav-glassmorphism .mobile-menu { display: none; flex-direction: column; gap: 1rem; padding: 1rem; background: rgba(20, 20, 30, 0.8); backdrop-filter: blur(10px); -webkit-backdrop-filter: blur(10px); position: absolute; top: 100%; left: 0; right: 0; z-index: 99; text-align: center; }
.nav-glassmorphism .mobile-menu a { color: var(--color-light, #FFFFFF); }
.nav-glassmorphism.open .mobile-menu { display: flex; }
@media(min-width: 768px) { .nav-glassmorphism .nav-links, .nav-glassmorphism .cta-button { display: flex; } .nav-glassmorphism .mobile-menu-toggle { display: none; } }
