css:
.nav-search-bar { font-family: sans-serif; position: relative; background-color: var(--color-light, #FFFFFF); padding: 1rem 0; border-bottom: 1px solid #e5e7eb; }
.nav-search-bar .nav-container { display: flex; justify-content: space-between; align-items: center; max-width: 1200px; margin: 0 auto; padding: 0 1rem; }
.nav-search-bar .logo { font-size: 1.5rem; font-weight: 700; color: var(--color-dark, #111827); text-decoration: none; }
.nav-search-bar .nav-links { display: none; gap: 2rem; }
.nav-search-bar .nav-links a { color: var(--color-dark, #111827); font-weight: 500; text-decoration: none; }
.nav-search-bar .search-form { display: none; position: relative; }
.nav-search-bar .search-form input { background-color: #f3f4f6; border: 1px solid #e5e7eb; border-radius: 9999px; padding: 0.5rem 1rem 0.5rem 2.5rem; font-size: 0.9rem; }
.nav-search-bar .search-form button { position: absolute; left: 0.5rem; top: 50%; transform: translateY(-50%); background: none; border: none; color: var(--color-secondary, #6B7280); }
.nav-search-bar .mobile-menu-toggle { display: block; background: none; border: none; cursor: pointer; }
.nav-search-bar .mobile-menu-toggle span { display: block; width: 25px; height: 2px; background-color: var(--color-dark, #111827); margin: 5px 0; }
.nav-search-bar .mobile-menu { display: none; flex-direction: column; gap: 1rem; padding: 1rem; background-color: var(--color-light, #FFFFFF); position: absolute; top: 100%; left: 0; right: 0; z-index: 99; text-align: center; }
.nav-search-bar.open .mobile-menu { display: flex; }
@media(min-width: 768px) { .nav-search-bar .nav-links, .nav-search-bar .search-form { display: flex; } .nav-search-bar .mobile-menu-toggle { display: none; } }
