css:
.nav-social-top-bar { font-family: sans-serif; position: relative; }
.nav-social-top-bar .nav-container { display: flex; justify-content: space-between; align-items: center; max-width: 1200px; margin: 0 auto; padding: 0 1rem; }
.nav-social-top-bar .top-bar { background-color: var(--color-primary, #4B88D7); color: var(--color-light, #FFFFFF); padding: 0.6rem 0; font-size: 0.9rem; }
.nav-social-top-bar .top-bar .social-links { display: flex; gap: 1rem; }
.nav-social-top-bar .top-bar a { color: var(--color-light, #FFFFFF); text-decoration: none; }
.nav-social-top-bar .top-bar-cta { font-weight: 600; }
.nav-social-top-bar .main-nav { background-color: var(--color-light, #FFFFFF); padding: 1.5rem 0; }
.nav-social-top-bar .logo { font-size: 1.8rem; font-weight: 700; color: var(--color-dark, #111827); text-decoration: none; }
.nav-social-top-bar .nav-links { display: none; gap: 2rem; }
.nav-social-top-bar .nav-links a { color: var(--color-dark, #111827); font-weight: 500; text-decoration: none; }
.nav-social-top-bar .mobile-menu-toggle { display: block; background: none; border: none; cursor: pointer; }
.nav-social-top-bar .mobile-menu-toggle span { display: block; width: 25px; height: 2px; background-color: var(--color-dark, #111827); margin: 5px 0; }
.nav-social-top-bar .mobile-menu { display: none; flex-direction: column; gap: 1rem; padding: 1rem; background-color: var(--color-light, #FFFFFF); position: absolute; top: 100%; left: 0; right: 0; z-index: 99; text-align: center; }
.nav-social-top-bar.open .mobile-menu { display: flex; }
@media(min-width: 768px) { .nav-social-top-bar .nav-links { display: flex; } .nav-social-top-bar .mobile-menu-toggle { display: none; } }
