css:
.nav-split-cta { font-family: sans-serif; position: relative; background-color: var(--color-light, #FFFFFF); padding: 1rem 0; border-bottom: 1px solid #e5e7eb; }
.nav-split-cta .nav-container { display: flex; justify-content: space-between; align-items: center; max-width: 1200px; margin: 0 auto; padding: 0 1rem; }
.nav-split-cta .logo { font-size: 1.5rem; font-weight: 700; color: var(--color-dark, #111827); text-decoration: none; }
.nav-split-cta .nav-links { display: none; gap: 2rem; }
.nav-split-cta .nav-links a { color: var(--color-secondary, #6B7280); font-weight: 500; text-decoration: none; transition: color 0.3s; }
.nav-split-cta .nav-links a:hover { color: var(--color-dark, #111827); }
.nav-split-cta .cta-button { display: none; padding: 0.6rem 1.2rem; background-color: var(--color-primary, #4B88D7); color: var(--color-light, #FFFFFF); border-radius: 6px; text-decoration: none; font-weight: 500; }
.nav-split-cta .mobile-menu-toggle { display: block; background: none; border: none; cursor: pointer; }
.nav-split-cta .mobile-menu-toggle span { display: block; width: 25px; height: 2px; background-color: var(--color-dark, #111827); margin: 5px 0; transition: all 0.3s; }
.nav-split-cta .mobile-menu { display: none; flex-direction: column; gap: 1rem; padding: 1rem; background-color: var(--color-light, #FFFFFF); position: absolute; top: 100%; left: 0; right: 0; z-index: 99; border-bottom: 1px solid #e5e7eb; text-align: center; }
.nav-split-cta .mobile-menu .cta-button { display: inline-block; margin-top: 1rem; }
.nav-split-cta.open .mobile-menu { display: flex; }
@media(min-width: 768px) { .nav-split-cta .nav-links, .nav-split-cta .cta-button { display: flex; } .nav-split-cta .mobile-menu-toggle { display: none; } }
