css:
.nav-studio-header {
    background-color: var(--color-bg, #090A0F);
    color: var(--color-light, #FFFFFF);
    padding: 0.75rem 0;
    border-bottom: 1px solid var(--color-border, #333333);
    position: relative;
    font-family: 'Inter', sans-serif;
}
.nav-studio-header .nav-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 1rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
}
.nav-studio-header .logo {
    font-size: 1.25rem;
    font-weight: 700;
    text-decoration: none;
    color: var(--color-light, #FFFFFF);
}
.nav-studio-header .logo-accent {
    color: var(--color-primary, #FF6A00);
}
.nav-studio-header .nav-links,
.nav-studio-header .nav-actions {
    display: none; /* Hidden on mobile */
}
@media (min-width: 768px) {
    .nav-studio-header .nav-links {
        display: flex;
        gap: 2rem;
        align-items: center;
    }
    .nav-studio-header .nav-actions {
        display: flex;
        gap: 1rem;
        align-items: center;
    }
}
.nav-studio-header .nav-links a {
    text-decoration: none;
    color: var(--color-secondary, #a0a0a0);
    font-weight: 500;
    transition: color 0.3s ease;
}
.nav-studio-header .nav-links a:hover {
    color: var(--color-light, #FFFFFF);
}
.nav-studio-header .btn {
    padding: 0.5rem 1.25rem;
    border-radius: 0.5rem;
    text-decoration: none;
    font-weight: 600;
    font-size: 0.9rem;
    transition: all 0.3s ease;
}
.nav-studio-header .btn-secondary {
    background-color: var(--color-surface-2, #2d2d2d);
    color: var(--color-light, #FFFFFF);
}
.nav-studio-header .btn-secondary:hover {
    background-color: var(--color-surface-1, #1e1e1e);
}
.nav-studio-header .btn-primary {
    background-color: var(--color-primary, #FF6A00);
    color: var(--color-dark, #000000);
}
.nav-studio-header .btn-primary:hover {
    opacity: 0.9;
}
.nav-studio-header .mobile-menu-toggle {
    display: block;
    background: none;
    border: none;
    cursor: pointer;
    padding: 0.5rem;
    z-index: 1001;
}
@media (min-width: 768px) {
    .nav-studio-header .mobile-menu-toggle {
        display: none;
    }
}
.nav-studio-header .mobile-menu-toggle span {
    display: block;
    width: 24px;
    height: 2px;
    background-color: var(--color-light, #FFFFFF);
    margin: 5px 0;
    transition: all 0.3s ease-in-out;
}
.nav-studio-header .mobile-menu {
    display: none;
    flex-direction: column;
    gap: 1.5rem;
    align-items: center;
    padding: 6rem 2rem 2rem;
    background-color: var(--color-bg, #090A0F);
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    z-index: 99;
}
.nav-studio-header .mobile-menu a {
    color: var(--color-light, #FFFFFF);
    font-size: 1.2rem;
    font-weight: 500;
}
.nav-studio-header .mobile-menu hr {
    width: 80%;
    border: none;
    border-top: 1px solid var(--color-border, #333333);
    margin: 1rem 0;
}
.nav-studio-header.open .mobile-menu {
    display: flex;
}
.nav-studio-header.open .mobile-menu-toggle {
    position: fixed;
    top: 1rem;
    right: 1rem;
}
.nav-studio-header.open .mobile-menu-toggle span:nth-child(1) {
    transform: rotate(45deg) translate(5px, 5px);
}
.nav-studio-header.open .mobile-menu-toggle span:nth-child(2) {
    opacity: 0;
}
.nav-studio-header.open .mobile-menu-toggle span:nth-child(3) {
    transform: rotate(-45deg) translate(5px, -5px);
}
