css:
.nav-with-socials { background-color: var(--color-light, #FFFFFF); padding: 1.5rem 0; position: relative; }
.nav-with-socials .nav-container { max-width: 1200px; margin: 0 auto; padding: 0 1rem; display: flex; align-items: center; }
.nav-with-socials .logo { font-size: 2rem; font-weight: 700; color: var(--color-dark, #1F2937); text-decoration: none; }
.nav-with-socials .nav-links { display: none; gap: 1.5rem; margin-left: 2rem; }
@media (min-width: 992px) { .nav-with-socials .nav-links { display: flex; } }
.nav-with-socials .nav-links a { text-decoration: none; color: var(--color-dark, #1F2937); font-weight: 500; }
.nav-with-socials .social-icons { display: none; margin-left: auto; gap: 1.5rem; align-items: center; }
@media (min-width: 992px) { .nav-with-socials .social-icons { display: flex; } }
.nav-with-socials .social-icons a { color: var(--color-dark, #1F2937); font-size: 1.2rem; }
.nav-with-socials .mobile-menu-toggle { display: block; margin-left: auto; background: none; border: none; cursor: pointer; z-index: 100; }
@media (min-width: 992px) { .nav-with-socials .mobile-menu-toggle { display: none; } }
.nav-with-socials .mobile-menu-toggle span { display: block; width: 25px; height: 2px; background-color: var(--color-dark, #1F2937); margin: 5px 0; transition: all 0.3s;}
.nav-with-socials .mobile-menu { display: none; flex-direction: column; gap: 1.5rem; padding: 2rem; background-color: var(--color-light, #FFFFFF); position: absolute; top: 100%; left: 0; right: 0; text-align: center; z-index: 99; }
.nav-with-socials.open .mobile-menu { display: flex; }
.nav-with-socials.open .mobile-menu-toggle span:nth-child(1) { transform: rotate(45deg) translate(5px, 5px); }
.nav-with-socials.open .mobile-menu-toggle span:nth-child(2) { opacity: 0; }
.nav-with-socials.open .mobile-menu-toggle span:nth-child(3) { transform: rotate(-45deg) translate(5px, -5px); }
