css:
.nav-header-basic { background-color: var(--color-light, #FFFFFF); padding: 1rem 0; position: relative; }
.nav-header-basic .nav-container { max-width: 1200px; margin: 0 auto; padding: 0 1rem; display: flex; justify-content: space-between; align-items: center; }
.nav-header-basic .logo { font-size: 2rem; font-weight: 700; color: var(--color-dark, #1F2937); text-decoration: none; }
.nav-header-basic .nav-links { display: none; gap: 2rem; }
@media (min-width: 992px) { .nav-header-basic .nav-links { display: flex; } }
.nav-header-basic .nav-links a { text-decoration: none; color: var(--color-dark, #1F2937); font-weight: 500; }
.nav-header-basic .cta-button { display: none; background-color: #2D3748; color: white; padding: 0.75rem 1.5rem; border-radius: 0.5rem; text-decoration: none; font-weight: 600; box-shadow: 0 4px 15px rgba(0,0,0,0.1); }
@media (min-width: 992px) { .nav-header-basic .cta-button { display: inline-block; } }
.nav-header-basic .mobile-menu-toggle { display: block; background: none; border: none; cursor: pointer; z-index: 100; }
@media (min-width: 992px) { .nav-header-basic .mobile-menu-toggle { display: none; } }
.nav-header-basic .mobile-menu-toggle span { display: block; width: 25px; height: 2px; background-color: var(--color-dark, #1F2937); margin: 5px 0; transition: all 0.3s; }
.nav-header-basic .mobile-menu { display: none; flex-direction: column; gap: 1.5rem; padding: 6rem 2rem 2rem; background-color: var(--color-light, #FFFFFF); position: absolute; top: 0; left: 0; right: 0; text-align: center; z-index: 99; }
.nav-header-basic .mobile-menu a { text-decoration: none; color: var(--color-dark, #1F2937); font-weight: 500; font-size: 1.2rem; }
.nav-header-basic .mobile-menu .cta-button { display: inline-block; margin-top: 1rem; }
.nav-header-basic.open .mobile-menu { display: flex; }
.nav-header-basic.open .mobile-menu-toggle { position: fixed; top: 1.5rem; right: 1rem; }
.nav-header-basic.open .mobile-menu-toggle span:nth-child(1) { transform: rotate(45deg) translate(5px, 5px); }
.nav-header-basic.open .mobile-menu-toggle span:nth-child(2) { opacity: 0; }
.nav-header-basic.open .mobile-menu-toggle span:nth-child(3) { transform: rotate(-45deg) translate(5px, -5px); }
