css:
.pricing-3-tier-section .container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}
.pricing-3-tier-section { padding: 4rem 0; background-color: var(--color-light, #FFFFFF); }
.pricing-3-tier-section .section-title { text-align: center; font-size: 2.5rem; margin-bottom: 3rem; color: var(--color-dark, #111827); }
.pricing-3-tier-section .pricing-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(280px, 1fr)); gap: 2rem; align-items: center; }
.pricing-3-tier-section .pricing-card {
    background: var(--color-light, #fff);
    border: 1px solid #e0e0e0;
    border-radius: 12px;
    padding: 2rem;
    text-align: center;
    transition: transform 0.3s, box-shadow 0.3s;
}
.pricing-3-tier-section .pricing-card:hover { transform: translateY(-10px); box-shadow: 0 15px 40px rgba(0,0,0,0.1); }
.pricing-3-tier-section .pricing-card.popular { border-color: var(--color-primary, #667eea); transform: scale(1.05); position: relative; }
.pricing-3-tier-section .popular-badge {
    position: absolute;
    top: -15px;
    left: 50%;
    transform: translateX(-50%);
    background: var(--color-primary, #667eea);
    color: var(--color-light, #FFFFFF);
    padding: 0.25rem 1rem;
    border-radius: 20px;
    font-size: 0.8rem;
    font-weight: 600;
}
.pricing-3-tier-section .pricing-card h3 { font-size: 1.2rem; text-transform: uppercase; letter-spacing: 1px; margin-bottom: 1rem; }
.pricing-3-tier-section .price { font-size: 3rem; font-weight: 700; margin-bottom: 1.5rem; color: var(--color-dark, #111827); }
.pricing-3-tier-section .price span { font-size: 1rem; font-weight: 400; color: var(--color-secondary, #6B7280); vertical-align: middle; }
.pricing-3-tier-section .pricing-card ul { list-style: none; padding: 0; margin-bottom: 2rem; }
.pricing-3-tier-section .pricing-card li { margin-bottom: 0.75rem; color: var(--color-secondary, #6B7280); }
.pricing-3-tier-section .btn-pricing { display: block; width: 100%; padding: 1rem; border: 1px solid var(--color-dark, #111827); color: var(--color-dark, #111827); text-decoration: none; border-radius: 8px; font-weight: 600; }
.pricing-3-tier-section .btn-pricing-popular { display: block; width: 100%; padding: 1rem; background-color: var(--color-primary, #667eea); color: var(--color-light, #FFFFFF); text-decoration: none; border-radius: 8px; font-weight: 600; }
