css:
.pricing-flip-section { padding: 5rem 0; background: #f1f5f9; }
.pricing-flip-section .container { max-width: 1100px; margin: 0 auto; text-align: center; }
.pricing-flip-section .section-title { font-size: 2.5rem; margin-bottom: 3rem; }
.pricing-flip-section .pricing-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 2rem; perspective: 1000px; }
.pricing-flip-section .flip-card { background-color: transparent; width: 100%; height: 400px; }
.pricing-flip-section .flip-card-inner { position: relative; width: 100%; height: 100%; transition: transform 0.8s; transform-style: preserve-3d; }
.pricing-flip-section .flip-card:hover .flip-card-inner { transform: rotateY(180deg); }
.pricing-flip-section .flip-card-front, .pricing-flip-section .flip-card-back { position: absolute; width: 100%; height: 100%; -webkit-backface-visibility: hidden; backface-visibility: hidden; border-radius: 1rem; display: flex; flex-direction: column; justify-content: center; align-items: center; padding: 2rem; }
.pricing-flip-section .flip-card-front { background: linear-gradient(135deg, #60a5fa, #3b82f6); color: white; }
.pricing-flip-section .flip-card-back { background: #fff; transform: rotateY(180deg); }
.pricing-flip-section h3 { font-size: 1.8rem; }
.pricing-flip-section .price { font-size: 3rem; font-weight: 700; }
.pricing-flip-section ul { list-style: none; padding: 0; margin: 1.5rem 0; }
.pricing-flip-section li { margin-bottom: 0.5rem; }
.pricing-flip-section .btn { padding: 0.8rem 2rem; border: 1px solid #ccc; text-decoration: none; border-radius: 8px; color: #333; }
