css:
.pricing-accordion-section { padding: 5rem 0; font-family: 'Inter', sans-serif; }
.pricing-accordion-section .container { max-width: 800px; margin: 0 auto; padding: 0 1rem; }
.pricing-accordion-section .section-title { text-align: center; font-size: 2.5rem; margin-bottom: 3rem; }
.pricing-accordion-section .accordion-wrapper { display: flex; flex-direction: column; gap: 1rem; }
.pricing-accordion-section .accordion-item { border: 1px solid #e5e7eb; border-radius: 1rem; }
.pricing-accordion-section .accordion-item summary {
    display: grid;
    grid-template-columns: 2fr 1fr auto;
    align-items: center;
    padding: 1.5rem;
    cursor: pointer;
    list-style: none;
}
.pricing-accordion-section .accordion-item summary::-webkit-details-marker { display: none; }
.pricing-accordion-section .plan-name { text-align: left; }
.pricing-accordion-section .plan-name h3 { font-size: 1.25rem; margin: 0; }
.pricing-accordion-section .popular-tag { font-size: 0.7rem; background: #3b82f6; color: #fff; padding: 0.2rem 0.5rem; border-radius: 99px; margin-left: 0.5rem; }
.pricing-accordion-section .plan-price { font-size: 1.5rem; font-weight: 700; text-align: right; }
.pricing-accordion-section .plan-arrow { width: 12px; height: 12px; border-top: 2px solid #9ca3af; border-right: 2px solid #9ca3af; transform: rotate(135deg); transition: transform 0.3s; }
.pricing-accordion-section .accordion-item[open] > summary .plan-arrow { transform: rotate(-45deg); }
.pricing-accordion-section .accordion-content { padding: 0 1.5rem 1.5rem; border-top: 1px solid #e5e7eb; }
.pricing-accordion-section .accordion-content p { color: #6b7280; font-size: 0.9rem; margin-bottom: 1.5rem; }
.pricing-accordion-section .accordion-content ul { list-style: none; padding: 0; margin-bottom: 1.5rem; }
.pricing-accordion-section .accordion-content li { margin-bottom: 0.5rem; }
.pricing-accordion-section .btn { display: block; text-align: center; padding: 0.8rem; border: 1px solid #111; border-radius: 8px; text-decoration: none; color: #111; }
.pricing-accordion-section .btn-primary { background: #3b82f6; color: #fff; border: none; }
