css:
.pricing-angled-section { padding: 5rem 0; background-color: #f1f5f9; font-family: 'Inter', sans-serif; }
.pricing-angled-section .container { max-width: 1100px; margin: 0 auto; padding: 0 1rem; text-align: center; }
.pricing-angled-section .section-title { font-size: 2.5rem; margin-bottom: 3rem; }
.pricing-angled-section .pricing-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 2rem; }
.pricing-angled-section .pricing-card { background: #fff; border-radius: 1rem; overflow: hidden; box-shadow: 0 10px 25px rgba(0,0,0,0.1); text-align: center; }
.pricing-angled-section .card-header { background: #3b82f6; color: #fff; padding: 2rem 1rem 4rem; clip-path: polygon(0 0, 100% 0, 100% 75%, 0 100%); }
.pricing-angled-section .pricing-card.popular .card-header { background: #8b5cf6; }
.pricing-angled-section .icon { font-size: 2.5rem; margin-bottom: 1rem; }
.pricing-angled-section h3 { font-size: 1.5rem; font-weight: 600; }
.pricing-angled-section .card-body { padding: 2rem; margin-top: -2rem; }
.pricing-angled-section .price { font-size: 3rem; font-weight: 700; margin-bottom: 1.5rem; }
.pricing-angled-section ul { list-style: none; padding: 0; margin-bottom: 2rem; color: #64748b; }
.pricing-angled-section li { margin-bottom: 0.5rem; }
.pricing-angled-section .btn { display: inline-block; padding: 0.8rem 2rem; border: 1px solid #cbd5e1; color: #334155; text-decoration: none; border-radius: 99px; }
.pricing-angled-section .btn-primary { background-color: #8b5cf6; color: #fff; border: none; }
