css:
.pricing-blocky-section { padding: 5rem 0; font-family: 'Inter', sans-serif; text-transform: uppercase; }
.pricing-blocky-section .container { display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); max-width: 1400px; margin: 0 auto; }
.pricing-blocky-section .pricing-card { padding: 3rem; display: flex; flex-direction: column; justify-content: space-between; min-height: 450px; }
.pricing-blocky-section .plan-1 { background-color: #f3f4f6; color: #111; }
.pricing-blocky-section .plan-2 { background-color: #3b82f6; color: #fff; }
.pricing-blocky-section .plan-3 { background-color: #111827; color: #fff; }
.pricing-blocky-section .price { font-size: 4rem; font-weight: 900; line-height: 1; }
.pricing-blocky-section h3 { font-size: 1rem; font-weight: 700; letter-spacing: 2px; margin: 1rem 0; }
.pricing-blocky-section p { font-size: 1rem; text-transform: none; opacity: 0.8; flex-grow: 1; }
.pricing-blocky-section .btn { display: inline-block; padding: 1rem 2rem; border: 2px solid; text-decoration: none; font-weight: 700; margin-top: 2rem; align-self: flex-start; }
.pricing-blocky-section .plan-1 .btn { border-color: #111; color: #111; }
.pricing-blocky-section .plan-2 .btn { border-color: #fff; color: #fff; }
.pricing-blocky-section .plan-3 .btn { border-color: #fff; color: #fff; }
